<?php

namespace fascli\BdcBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * dollarRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class dollarRepository extends EntityRepository
{
	public function getLignesForDollars($clientId)
    {
        
        $qb = $this->createQueryBuilder('a')
                   ->select('a')
                   ->where('a.operationsbdc_id = :client_id')
                   ->setParameter('client_id', $clientId)
                   ->addOrderBy('a.id',"ASC");
           
        return $qb->getQuery()
                  ->getResult();
    }


    public function getAchatsDeviseCoupure($devise, $coupure, $datope){
     $now = new \DateTime();
    $nowd = $now->format('d');
    $nowm = $now->format('m');
     $qb = $this->_em->createQueryBuilder();
    $qb->select('e') 
    ->from('fascliBdcBundle:dollar', 'e')
    ->InnerJoin('e.operationbdc', 'o')
    ->addSelect('o')
    ->where('e.type = :typ')
    ->setParameter('typ', 'A')
    ->andwhere('e.val = :val')
    ->setParameter('val', $coupure)
    ->andwhere('o.devise= :dev')
    ->setParameter('dev', $devise)
    ->andwhere('o.datope <= :dat')
    ->setParameter('dat', $datope)
    ->andwhere('e.qterest > :qte')
    ->setParameter('qte', '0');
     $bbb = $qb->getQuery()
            ->getResult();

     return $bbb;
  }

  public function getVentesDeviseCoupure($devise, $coupure){
     
     $qb = $this->_em->createQueryBuilder();
    $qb->select('e') 
    ->from('fascliBdcBundle:dollar', 'e')
    ->InnerJoin('e.operationbdc', 'o')
    ->addSelect('o')
    ->where('e.type = :typ')
    ->setParameter('typ', 'V')
    ->andwhere('e.val = :val')
    ->setParameter('val', $coupure)
    ->andwhere('o.devise= :dev')
    ->setParameter('dev', $devise)
    ->andwhere('e.qterest > :qte')
    ->setParameter('qte', '0');
     $bbb = $qb->getQuery()
            ->getResult();

     return $bbb;
  }
}
